import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, GeoJSON, ZoomControl, AttributionControl } from 'react-leaflet';
import L from 'leaflet';

// 自定义标记图标 - 不同颜色表示不同图层
const layerIcons = {
  culture: L.icon({
    iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
    shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowSize: [41, 41],
    className: 'marker-culture'
  }),
  history: L.icon({
    iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
    shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowSize: [41, 41],
    className: 'marker-history'
  }),
  lifestyle: L.icon({
    iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
    shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowSize: [41, 41],
    className: 'marker-lifestyle'
  }),
  base: L.icon({
    iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
    shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowSize: [41, 41]
  })
};

// 为不同图层的标记添加CSS颜色样式
const style = document.createElement('style');
style.textContent = `
  .marker-culture img { filter: hue-rotate(200deg); }
  .marker-history img { filter: hue-rotate(300deg); }
  .marker-lifestyle img { filter: hue-rotate(100deg); }
`;
document.head.appendChild(style);

// 模拟数据 - 人文地理标记点
const 人文地理数据 = [
  {
    id: 1,
    name: '北京故宫',
    lat: 39.9163,
    lng: 116.3972,
    type: '历史古迹',
    layer: 'history',
    description: '明清两代的皇家宫殿，世界最大的木质结构古建筑群。',
    details: [
      '世界文化遗产',
      '建于1406-1420年',
      '占地面积约72万平方米'
    ]
  },
  {
    id: 2,
    name: '巴黎埃菲尔铁塔',
    lat: 48.8584,
    lng: 2.2945,
    type: '建筑地标',
    layer: 'culture',
    description: '法国巴黎的标志性建筑，1889年为巴黎世博会而建。',
    details: [
      '高324米',
      '由古斯塔夫·埃菲尔设计',
      '每年约700万游客'
    ]
  },
  {
    id: 3,
    name: '日本京都金阁寺',
    lat: 35.0394,
    lng: 135.7298,
    type: '宗教建筑',
    layer: 'culture',
    description: '日本最著名的佛教寺庙之一，以金箔装饰的建筑闻名。',
    details: [
      '正式名称：鹿苑寺',
      '建于1397年',
      '1950年曾被烧毁，1955年重建'
    ]
  },
  {
    id: 4,
    name: '意大利罗马斗兽场',
    lat: 41.8902,
    lng: 12.4922,
    type: '历史古迹',
    layer: 'history',
    description: '古罗马时期最大的圆形角斗场，世界七大奇迹之一。',
    details: [
      '建于公元70-80年',
      '可容纳5-8万名观众',
      '主要用于角斗士比赛和公共表演'
    ]
  },
  {
    id: 5,
    name: '中国四川大熊猫基地',
    lat: 30.7475,
    lng: 104.1091,
    type: '自然文化',
    layer: 'lifestyle',
    description: '全球最大的大熊猫保护研究机构，致力于大熊猫的繁育和保护。',
    details: [
      '成立于1987年',
      '占地面积约92英亩',
      '现有约150只大熊猫'
    ]
  },
  {
    id: 6,
    name: '印度泰姬陵',
    lat: 27.1751,
    lng: 78.0421,
    type: '建筑地标',
    layer: 'culture',
    description: '莫卧儿王朝皇帝沙贾汗为纪念爱妃而建的白色大理石陵墓。',
    details: [
      '世界文化遗产',
      '建于1632-1653年',
      '被誉为"完美建筑"'
    ]
  }
];

const MapComponent = ({ onLocationSelect, activeLayer }) => {
  const [filteredData, setFilteredData] = useState([]);
  const [clickedCoord, setClickedCoord] = useState(null);

  useEffect(() => {
    // 根据当前激活的图层过滤数据
    if (activeLayer === 'base') {
      setFilteredData(人文地理数据);
    } else {
      setFilteredData(人文地理数据.filter(item => item.layer === activeLayer));
    }
  }, [activeLayer]);

  const handleMarkerClick = (location) => {
    onLocationSelect(location);
    setClickedCoord(null); // 点击标记时清除点击坐标
  };

  const handleMapClick = (e) => {
    const { lat, lng } = e.latlng;
    setClickedCoord({ lat: lat.toFixed(4), lng: lng.toFixed(4) });
    onLocationSelect(null); // 点击地图空白处时清除选中位置
  };

  return (
    <div className="map-container">
      <MapContainer 
        center={[20, 0]} 
        zoom={2} 
        style={{ height: '100%', width: '100%' }}
        zoomControl={false}
        attributionControl={false}
        doubleClickZoom={true}
        scrollWheelZoom={true}
        dragging={true}
        eventHandlers={{ click: handleMapClick }}
      >
        <ZoomControl position="topright" />
        <AttributionControl position="bottomright" />
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />

        {filteredData.map(location => (
          <Marker
            key={location.id}
            position={[location.lat, location.lng]}
            icon={layerIcons[location.layer] || layerIcons.base}
            eventHandlers={{ click: () => handleMarkerClick(location) }}
          >
            <Popup>
              <div>
                <h3>{location.name}</h3>
                <p>{location.description}</p>
                <button onClick={() => handleMarkerClick(location)}>
                  查看详情
                </button>
              </div>
            </Popup>
          </Marker>
        ))}

        {/* 显示点击位置的坐标 */}
        {clickedCoord && (
          <Popup
            position={[parseFloat(clickedCoord.lat), parseFloat(clickedCoord.lng)]}
            closeButton={false}
            autoClose={false}
          >
            <div>
              <p><strong>坐标:</strong></p>
              <p>纬度: {clickedCoord.lat}</p>
              <p>经度: {clickedCoord.lng}</p>
              <button onClick={() => setClickedCoord(null)}>关闭</button>
            </div>
          </Popup>
        )}
      </MapContainer>
    </div>
  );
};

export default MapComponent;
