import locationsData from './locations.json';
import articlesData from './articles.json';
import mediaData from './media.json';
import categoriesData from './categories.json';
import tagsData from './tags.json';
import usersData from './users.json';
import settingsData from './settings.json';
import apiService from '../services/apiService';

class DataManager {
  constructor() {
    this.locations = locationsData.locations;
    this.locationsMetadata = locationsData.metadata;

    // 文章数据
    this.articles = articlesData.articles;
    this.articlesMetadata = articlesData.metadata;

    // 媒体数据
    this.media = mediaData.media;
    this.mediaFolders = mediaData.folders;
    this.mediaMetadata = mediaData.metadata;

    // 分类与标签
    this.categories = categoriesData.categories;
    this.categoriesMetadata = categoriesData.metadata;
    this.tags = tagsData.tags;
    this.tagsMetadata = tagsData.metadata;

    // 用户与权限
    this.users = usersData.users;
    this.roles = usersData.roles;
    this.permissions = usersData.permissions;
    this.rolePermissions = usersData.role_permissions;
    this.usersMetadata = usersData.metadata;

    // 系统设置
    this.websiteSettings = settingsData.website_settings;
    this.emailSettings = settingsData.email_settings;
    this.seoSettings = settingsData.seo_settings;
    this.securitySettings = settingsData.security_settings;
    this.operationLogs = settingsData.logs.operation_logs;
    this.errorLogs = settingsData.logs.error_logs;
    this.settingsMetadata = settingsData.metadata;
  }

  // 获取所有地点
  getAllLocations() {
    return this.locations;
  }

  // 根据图层获取地点
  getLocationsByLayer(layer) {
    if (!layer || layer === 'all') {
      return this.locations;
    }
    return this.locations.filter(location => location.layer === layer);
  }

  // 根据ID获取地点
  getLocationById(id) {
    return this.locations.find(location => location.id === id);
  }

  // 根据标签获取地点
  getLocationsByTag(tag) {
    return this.locations.filter(location => location.tags && location.tags.includes(tag));
  }

  // 根据类型获取地点
  getLocationsByType(type) {
    return this.locations.filter(location => location.type === type);
  }

  // 搜索地点
  searchLocations(query, language = 'zh') {
    const lowerQuery = query.toLowerCase();
    return this.locations.filter(location => 
      location.name[language].toLowerCase().includes(lowerQuery) ||
      location.description[language].toLowerCase().includes(lowerQuery) ||
      (location.tags && location.tags.some(tag => tag.toLowerCase().includes(lowerQuery)))
    );
  }

  // 添加地点
  addLocation(location) {
    const newLocation = {
      ...location,
      id: `loc_${(this.locations.length + 1).toString().padStart(3, '0')}`,
      created_at: new Date().toISOString(),
      updated_at: new Date().toISOString()
    };
    this.locations.push(newLocation);
    this.metadata.total_locations = this.locations.length;
    this.metadata.last_updated = new Date().toISOString();
    return newLocation;
  }

  // 更新地点
  updateLocation(id, updates) {
    const index = this.locations.findIndex(location => location.id === id);
    if (index === -1) return null;

    this.locations[index] = {
      ...this.locations[index],
      ...updates,
      updated_at: new Date().toISOString()
    };

    this.metadata.last_updated = new Date().toISOString();
    return this.locations[index];
  }

  // 删除地点
  deleteLocation(id) {
    const index = this.locations.findIndex(location => location.id === id);
    if (index === -1) return false;

    this.locations.splice(index, 1);
    this.metadata.total_locations = this.locations.length;
    this.metadata.last_updated = new Date().toISOString();
    return true;
  }

  // 获取元数据
  getMetadata() {
    return this.metadata;
  }

  // 获取所有图层类型
  getLayers() {
    const layers = [...new Set(this.locations.map(location => location.layer))];
    return layers.sort();
  }

  // 获取所有地点类型
  getTypes() {
    const types = [...new Set(this.locations.map(location => location.type))];
    return types.sort();
  }

  // 获取所有标签
  getAllTags() {
    const tags = new Set();
    this.locations.forEach(location => {
      if (location.tags) {
        location.tags.forEach(tag => tags.add(tag));
      }
    });
    return Array.from(tags).sort();
  }

  // 文章管理相关方法
  getAllArticles() {
    return this.articles;
  }

  getArticleById(id) {
    return this.articles.find(article => article.id === id);
  }

  getArticlesByStatus(status) {
    if (!status) return this.articles;
    return this.articles.filter(article => article.status === status);
  }

  getArticlesByAuthor(authorId) {
    return this.articles.filter(article => article.author_id === authorId);
  }

