  getAllLocations() {
    return this.locations;
  }

  // 根据图层获取地点
  getLocationsByLayer(layer) {
    if (!layer || layer === 'all') {
      return this.locations;
    }
    return this.locations.filter(location => location.layer === layer);
  }

  // 根据ID获取地点
  getLocationById(id) {
    return this.locations.find(location => location.id === id);
  }

  // 根据标签获取地点
  getLocationsByTag(tag) {
    return this.locations.filter(location => location.tags && location.tags.includes(tag));
  }

  // 根据类型获取地点
  getLocationsByType(type) {
    return this.locations.filter(location => location.type === type);
  }

  // 搜索地点
  searchLocations(query, language = 'zh') {
    const lowerQuery = query.toLowerCase();
    return this.locations.filter(location => location.name[language].toLowerCase().includes(lowerQuery) || location.description[language].toLowerCase().includes(lowerQuery) || location.tags && location.tags.some(tag => tag.toLowerCase().includes(lowerQuery)));
  }

  // 添加地点
  addLocation(location) {
    const newLocation = {
      ...location,
      id: `loc_${(this.locations.length + 1).toString().padStart(3, '0')}`,
      created_at: new Date().toISOString(),
      updated_at: new Date().toISOString()
    };
    this.locations.push(newLocation);
    this.metadata.total_locations = this.locations.length;
    this.metadata.last_updated = new Date().toISOString();
    return newLocation;
  }

  // 更新地点
  updateLocation(id, updates) {
    const index = this.locations.findIndex(location => location.id === id);
    if (index === -1) return null;
    this.locations[index] = {
      ...this.locations[index],
      ...updates,
      updated_at: new Date().toISOString()
    };
    this.metadata.last_updated = new Date().toISOString();
    return this.locations[index];
  }

  // 删除地点
  deleteLocation(id) {
    const index = this.locations.findIndex(location => location.id === id);
    if (index === -1) return false;
    this.locations.splice(index, 1);
    this.metadata.total_locations = this.locations.length;
    this.metadata.last_updated = new Date().toISOString();
    return true;
  }

  // 获取元数据
  getMetadata() {
    return this.metadata;
  }

  // 获取所有图层类型
  getLayers() {
    const layers = [...new Set(this.locations.map(location => location.layer))];
    return layers.sort();
  }

  // 获取所有地点类型
  getTypes() {
    const types = [...new Set(this.locations.map(location => location.type))];
    return types.sort();
  }

  // 获取所有标签
  getAllTags() {
    const tags = new Set();
    this.locations.forEach(location => {
      if (location.tags) {
        location.tags.forEach(tag => tags.add(tag));
      }
    });
    return Array.from(tags).sort();
  }

  // 文章管理相关方法
  getAllArticles() {
    return this.articles;
  }
  getArticleById(id) {
    return this.articles.find(article => article.id === id);
  }
  getArticlesByStatus(status) {
    if (!status) return this.articles;
    return this.articles.filter(article => article.status === status);
  }
  getArticlesByAuthor(authorId) {
    return this.articles.filter(article => article.author_id === authorId);
  }
  getArticlesByCategory(categoryId) {
    return this.articles.filter(article => article.category_id === categoryId);
  }
  getArticlesByTag(tagId) {
    return this.articles.filter(article => article.tags && article.tags.includes(tagId));
  }
  searchArticles(query, language = 'zh') {
    const lowerQuery = query.toLowerCase();
    return this.articles.filter(article => article.title[language].toLowerCase().includes(lowerQuery) || article.content[language].toLowerCase().includes(lowerQuery));
  }
  addArticle(article) {
    const newArticle = {
      ...article,
      id: `article_${(this.articles.length + 1).toString().padStart(4, '0')}`,
      created_at: new Date().toISOString(),
      updated_at: new Date().toISOString(),
      status: 'draft'
    };
    this.articles.push(newArticle);
    this.articlesMetadata.total_articles = this.articles.length;
    this.articlesMetadata.last_updated = new Date().toISOString();
    return newArticle;
  }
  updateArticle(id, updates) {
    const index = this.articles.findIndex(article => article.id === id);
    if (index === -1) return null;
    this.articles[index] = {
      ...this.articles[index],
      ...updates,
      updated_at: new Date().toISOString()
    };
    this.articlesMetadata.last_updated = new Date().toISOString();
    return this.articles[index];
  }
  deleteArticle(id) {
    const index = this.articles.findIndex(article => article.id === id);
    if (index === -1) return false;
    this.articles.splice(index, 1);
    this.articlesMetadata.total_articles = this.articles.length;
    this.articlesMetadata.last_updated = new Date().toISOString();
    return true;
  }

  // 媒体管理相关方法
  getAllMedia() {
    return this.media;
  }
  getMediaById(id) {
    return this.media.find(media => media.id === id);
  }
  getMediaByType(type) {
    if (!type) return this.media;
    return this.media.filter(media => media.type === type);
  }
  getMediaByFolder(folderId) {
    return this.media.filter(media => media.folder_id === folderId);
  }
  addMedia(media) {
    const newMedia = {
      ...media,
      id: `media_${(this.media.length + 1).toString().padStart(4, '0')}`,
      uploaded_at: new Date().toISOString(),
      size: media.size || 0
    };
    this.media.push(newMedia);
    this.mediaMetadata.total_files = this.media.length;
    this.mediaMetadata.last_updated = new Date().toISOString();
    return newMedia;
  }
  deleteMedia(id) {
    const index = this.media.findIndex(media => media.id === id);
    if (index === -1) return false;
    this.media.splice(index, 1);
    this.mediaMetadata.total_files = this.media.length;
    this.mediaMetadata.last_updated = new Date().toISOString();
    return true;
  }
  getAllMediaFolders() {
    return this.mediaFolders;
  }
  addMediaFolder(folder) {
    const newFolder = {
      ...folder,
      id: `folder_${(this.mediaFolders.length + 1).toString().padStart(3, '0')}`,
      created_at: new Date().toISOString()
    };
    this.mediaFolders.push(newFolder);
    return newFolder;
  }
  deleteMediaFolder(id) {
    // 检查文件夹是否为空
    const hasMedia = this.media.some(media => media.folder_id === id);
    if (hasMedia) return false;
    const index = this.mediaFolders.findIndex(folder => folder.id === id);
    if (index === -1) return false;
    this.mediaFolders.splice(index, 1);
    return true;
  }

  // 分类管理相关方法
  getAllCategories() {
    return this.categories;
  }
  getCategoryById(id) {
    return this.categories.find(category => category.id === id);
  }
  getCategoriesByParent(parentId) {
    return this.categories.filter(category => category.parent_id === parentId);
  }
  addCategory(category) {
    const newCategory = {
      ...category,
      id: `cat_${(this.categories.length + 1).toString().padStart(3, '0')}`,
      created_at: new Date().toISOString(),
      updated_at: new Date().toISOString()
    };
    this.categories.push(newCategory);
    this.categoriesMetadata.total_categories = this.categories.length;
    return newCategory;
  }
  updateCategory(id, updates) {
    const index = this.categories.findIndex(category => category.id === id);
    if (index === -1) return null;
    this.categories[index] = {
      ...this.categories[index],
      ...updates,
      updated_at: new Date().toISOString()
    };
    return this.categories[index];
  }
  deleteCategory(id) {
    // 检查是否有文章属于该分类
    const hasArticles = this.articles.some(article => article.category_id === id);
    if (hasArticles) return false;

    // 检查是否有子分类
    const hasChildren = this.categories.some(category => category.parent_id === id);
    if (hasChildren) return false;
    const index = this.categories.findIndex(category => category.id === id);
    if (index === -1) return false;
    this.categories.splice(index, 1);
    this.categoriesMetadata.total_categories = this.categories.length;
    return true;
  }

  // 标签管理相关方法
  getAllTags() {
    return this.tags;
  }
  getTagById(id) {
    return this.tags.find(tag => tag.id === id);
  }
  addTag(tag) {
    const newTag = {
      ...tag,
      id: `tag_${(this.tags.length + 1).toString().padStart(3, '0')}`,
      created_at: new Date().toISOString(),
      article_count: 0
    };
    this.tags.push(newTag);
    return newTag;
  }
  updateTag(id, updates) {
    const index = this.tags.findIndex(tag => tag.id === id);
    if (index === -1) return null;
    this.tags[index] = {
      ...this.tags[index],
      ...updates
    };
    return this.tags[index];
  }
  deleteTag(id) {
    const index = this.tags.findIndex(tag => tag.id === id);
    if (index === -1) return false;

    // 从所有文章中移除该标签
    this.articles.forEach(article => {
      if (article.tags && article.tags.includes(id)) {
        article.tags = article.tags.filter(tagId => tagId !== id);
      }
    });
    this.tags.splice(index, 1);
    return true;
  }

  // 用户管理相关方法
  async getAllUsers() {
    try {
      const users = await apiService.user.getAllUsers();
      // 更新本地缓存
      this.users = users;
      this.usersMetadata.total_users = users.length;
      return users;
    } catch (error) {
      console.error('获取用户列表失败:', error);
      return this.users;
    }
  }
  async getUserById(id) {
    try {
      const user = await apiService.user.getUserById(id);
      return user;
    } catch (error) {
      console.error(`获取用户 ${id} 失败:`, error);
      return this.users.find(user => user.id === id);
    }
  }
  async getUserByUsername(username) {
    try {
      const user = await apiService.user.getUserByUsername(username);
      return user;
    } catch (error) {
      console.error(`获取用户 ${username} 失败:`, error);
      return this.users.find(user => user.username === username);
    }
  }
  async addUser(user) {
    try {
      const newUser = await apiService.user.createUser(user);
      // 更新本地缓存
      this.users.push(newUser);
      this.usersMetadata.total_users = this.users.length;
      return newUser;
    } catch (error) {
      console.error('创建用户失败:', error);
      // 降级到本地处理
      const newUser = {
        ...user,
        id: `user_${(this.users.length + 1).toString().padStart(3, '0')}`,
        created_at: new Date().toISOString(),
        status: 'active'
      };
      this.users.push(newUser);
      this.usersMetadata.total_users = this.users.length;
      return newUser;
    }
  }
  async updateUser(id, updates) {
    try {
      const updatedUser = await apiService.user.updateUser(id, updates);
      // 更新本地缓存
      const index = this.users.findIndex(user => user.id === id);
      if (index !== -1) {
        this.users[index] = updatedUser;
      }
      return updatedUser;
    } catch (error) {
      console.error(`更新用户 ${id} 失败:`, error);
      // 降级到本地处理
      const index = this.users.findIndex(user => user.id === id);
      if (index === -1) return null;
      this.users[index] = {
        ...this.users[index],
        ...updates,
        updated_at: new Date().toISOString()
      };
      return this.users[index];
    }
  }
  async deleteUser(id) {
    try {
      const success = await apiService.user.deleteUser(id);
      // 更新本地缓存
      if (success) {
        const index = this.users.findIndex(user => user.id === id);
        if (index !== -1) {
          this.users.splice(index, 1);
          this.usersMetadata.total_users = this.users.length;
        }
      }
      return success;
    } catch (error) {
      console.error(`删除用户 ${id} 失败:`, error);
      // 降级到本地处理
      const index = this.users.findIndex(user => user.id === id);
      if (index === -1) return false;
      this.users.splice(index, 1);
      this.usersMetadata.total_users = this.users.length;
      return true;
    }
  }

  // 权限管理相关方法
  async getAllRoles() {
    try {
      const roles = await apiService.rolePermission.getAllRoles();
      // 更新本地缓存
      this.roles = roles;
      return roles;
    } catch (error) {
      console.error('获取角色列表失败:', error);
      return this.roles;
    }
  }
  async getRoleById(id) {
    try {
      // 先从本地缓存查找
      const role = this.roles.find(role => role.id === id);
      if (role) return role;

      // 如果本地没有，从API获取
      return await apiService.rolePermission.getRoleById(id);
    } catch (error) {
      console.error(`获取角色 ${id} 失败:`, error);
      return this.roles.find(role => role.id === id);
    }
