        // 删除该角色的所有现有权限
        this.rolePermissions = this.rolePermissions.filter(rp => rp.role_id !== roleId);

        // 添加新的权限关联
        permissionIds.forEach(permissionId => {
          this.rolePermissions.push({
            role_id: roleId,
            permission_id: permissionId
          });
        });
      }
      return success;
    } catch (error) {
      console.error(`更新角色 ${roleId} 权限失败:`, error);
      // 降级到本地处理
      // 删除该角色的所有现有权限
      this.rolePermissions = this.rolePermissions.filter(rp => rp.role_id !== roleId);

      // 添加新的权限关联
      permissionIds.forEach(permissionId => {
        this.rolePermissions.push({
          role_id: roleId,
          permission_id: permissionId
        });
      });
      return true;
    }
