  async getAllPermissions() {
    try {
      const permissions = await apiService.rolePermission.getAllPermissions();
      // 更新本地缓存
      this.permissions = permissions;
      return permissions;
    } catch (error) {
      console.error('获取权限列表失败:', error);
      return this.permissions;
    }
