    return this.emailSettings;
  }
  updateEmailSettings(updates) {
    this.emailSettings = {
      ...this.emailSettings,
      ...updates
    };
    return this.emailSettings;
  }
  getSEOSettings() {
    return this.seoSettings;
  }
  updateSEOSettings(updates) {
    this.seoSettings = {
      ...this.seoSettings,
      ...updates
    };
    return this.seoSettings;
  }
  getSecuritySettings() {
    return this.securitySettings;
  }
  updateSecuritySettings(updates) {
    this.securitySettings = {
      ...this.securitySettings,
      ...updates
    };
    return this.securitySettings;
  }

  // 日志管理相关方法
  getAllOperationLogs() {
    return this.operationLogs;
  }
  addOperationLog(log) {
    const newLog = {
      ...log,
      id: `log_${(this.operationLogs.length + 1).toString().padStart(5, '0')}`,
      timestamp: new Date().toISOString()
    };
    this.operationLogs.push(newLog);
    return newLog;
  }
  getAllErrorLogs() {
    return this.errorLogs;
  }
  addErrorLog(log) {
    const newLog = {
      ...log,
      id: `error_${(this.errorLogs.length + 1).toString().padStart(5, '0')}`,
      timestamp: new Date().toISOString()
    };
    this.errorLogs.push(newLog);
    return newLog;
  }

  // 更新日志（清空日志）
  clearOperationLogs() {
    this.operationLogs = [];
    return true;
  }
  clearErrorLogs() {
    this.errorLogs = [];
    return true;
  }
}

