import React, { useState, useEffect } from 'react';
import CMSNavbar from '../components/CMSNavbar';
import dataManager from '../data/dataManager';

const CMSLocationManagement = () => {
  const [locations, setLocations] = useState([]);
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [formData, setFormData] = useState({
    name: { zh: '', en: '' },
    description: { zh: '', en: '' },
    coordinates: { lat: '', lng: '' },
    layer: 'culture',
    type: '',
    tags: [],
    details: []
  });

  useEffect(() => {
    loadLocations();
  }, []);

  const loadLocations = () => {
    setLocations(dataManager.getAllLocations());
  };

  const handleAddLocation = () => {
    setSelectedLocation(null);
    setFormData({
      name: { zh: '', en: '' },
      description: { zh: '', en: '' },
      coordinates: { lat: '', lng: '' },
      layer: 'culture',
      type: '',
      tags: [],
      details: []
    });
    setShowAddForm(true);
  };

  const handleEditLocation = (location) => {
    setSelectedLocation(location);
    setFormData(location);
    setShowAddForm(true);
  };

  const handleDeleteLocation = (id) => {
    if (window.confirm('确定要删除这个地点吗？')) {
      dataManager.deleteLocation(id);
      loadLocations();
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (selectedLocation) {
      dataManager.updateLocation(selectedLocation.id, formData);
    } else {
      dataManager.addLocation(formData);
    }

    setShowAddForm(false);
    loadLocations();
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name.includes('.')) {
      // Handle nested fields like name.zh
      const [parent, child] = name.split('.');
      setFormData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleTagChange = (e) => {
    const tags = e.target.value.split(',').map(tag => tag.trim()).filter(Boolean);
    setFormData(prev => ({ ...prev, tags }));
  };

  const handleDetailChange = (index, value) => {
    const newDetails = [...formData.details];
    newDetails[index] = value;
    setFormData(prev => ({ ...prev, details: newDetails }));
  };

  const addDetailField = () => {
    setFormData(prev => ({ ...prev, details: [...prev.details, ''] }));
  };

  return (
    <div className="cms-location-management">
      <CMSNavbar />
      <div className="cms-container">
        <div className="cms-sidebar">
          <h3>管理菜单</h3>
          <ul>
            <li><a href="#dashboard">仪表板</a></li>
            <li><a href="#locations" className="active">位置管理</a></li>
            <li><a href="#layers">图层管理</a></li>
            <li><a href="#settings">系统设置</a></li>
            <li><a href="#logout">退出登录</a></li>
          </ul>
        </div>
        <div className="cms-content">
          <div className="cms-header">
            <h2>位置管理</h2>
            <button onClick={handleAddLocation} className="add-btn">添加新地点</button>
          </div>

          {showAddForm && (
            <div className="location-form-container">
              <h3>{selectedLocation ? '编辑地点' : '添加新地点'}</h3>
              <form onSubmit={handleSubmit} className="location-form">
                <div className="form-section">
                  <h4>基本信息</h4>
                  <div className="form-row">
                    <div className="form-group">
                      <label>中文名称:</label>
                      <input 
                        type="text" 
                        name="name.zh" 
                        value={formData.name.zh} 
                        onChange={handleInputChange} 
                        required 
                      />
                    </div>
                    <div className="form-group">
                      <label>英文名称:</label>
                      <input 
                        type="text" 
                        name="name.en" 
                        value={formData.name.en} 
                        onChange={handleInputChange} 
                      />
                    </div>
                  </div>
                  <div className="form-row">
                    <div className="form-group">
                      <label>中文描述:</label>
                      <textarea 
                        name="description.zh" 
                        value={formData.description.zh} 
                        onChange={handleInputChange} 
                        rows="3"
                        required 
                      />
                    </div>
                    <div className="form-group">
                      <label>英文描述:</label>
                      <textarea 
                        name="description.en" 
                        value={formData.description.en} 
                        onChange={handleInputChange} 
                        rows="3"
                      />
                    </div>
                  </div>
                </div>

                <div className="form-section">
                  <h4>位置信息</h4>
                  <div className="form-row">
                    <div className="form-group">
                      <label>纬度:</label>
                      <input 
                        type="number" 
                        name="coordinates.lat" 
                        value={formData.coordinates.lat} 
                        onChange={handleInputChange} 
                        step="0.0001"
                        required 
                      />
                    </div>
                    <div className="form-group">
                      <label>经度:</label>
                      <input 
                        type="number" 
                        name="coordinates.lng" 
                        value={formData.coordinates.lng} 
                        onChange={handleInputChange} 
                        step="0.0001"
                        required 
                      />
                    </div>
                  </div>
                  <div className="form-row">
                    <div className="form-group">
                      <label>图层:</label>
                      <select 
                        name="layer" 
                        value={formData.layer} 
                        onChange={handleInputChange} 
                      >
                        <option value="culture">文化层</option>
                        <option value="history">历史层</option>
                        <option value="lifestyle">生活层</option>
                        <option value="base">基础层</option>
                      </select>
                    </div>
                    <div className="form-group">
                      <label>类型:</label>
                      <input 
                        type="text" 
                        name="type" 
                        value={formData.type} 
                        onChange={handleInputChange} 
                      />
                    </div>
                  </div>
                </div>

                <div className="form-section">
                  <h4>标签和详情</h4>
                  <div className="form-group">
                    <label>标签 (用逗号分隔):</label>
                    <input 
                      type="text" 
                      value={formData.tags.join(', ')} 
                      onChange={handleTagChange} 
                      placeholder="例如: 文化, 历史, 旅游"
                    />
                  </div>
                  <div className="form-group">
                    <label>详情:</label>
                    {formData.details.map((detail, index) => (
                      <div key={index} className="detail-input-group">
                        <input 
                          type="text" 
                          value={detail} 
                          onChange={(e) => handleDetailChange(index, e.target.value)} 
                          placeholder={`详情 ${index + 1}`}
                        />
                      </div>
                    ))}
                    <button type="button" onClick={addDetailField} className="add-detail-btn">添加详情</button>
                  </div>
                </div>

                <div className="form-actions">
                  <button type="button" onClick={() => setShowAddForm(false)} className="cancel-btn">取消</button>
                  <button type="submit" className="save-btn">保存</button>
                </div>
              </form>
            </div>
          )}

          <div className="locations-table-container">
            <table className="locations-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>中文名称</th>
                  <th>英文名称</th>
                  <th>图层</th>
                  <th>类型</th>
                  <th>操作</th>
                </tr>
              </thead>
              <tbody>
                {locations.map(location => (
                  <tr key={location.id}>
                    <td>{location.id}</td>
                    <td>{location.name.zh}</td>
                    <td>{location.name.en}</td>
                    <td>{location.layer}</td>
                    <td>{location.type}</td>
                    <td>
                      <button onClick={() => handleEditLocation(location)} className="edit-btn">编辑</button>
                      <button onClick={() => handleDeleteLocation(location.id)} className="delete-btn">删除</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CMSLocationManagement;
